// Initial Data
export const initialState = {
    episode: []
};

const reducer = (state, action) => {
    // 
    console.log(action);

    switch(action.type) {
        case 'ADD-EPISODE':
            return {
                ...state,
                episode: [...state.episode, action.item],
            };
    }
}

export default reducer;
