import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Display.css';
import { useStateValue } from '../DataLayer';

function Display() {

    const [{ input }, dispatch] = useStateValue();
    const [episode, setEpisode] = useState({});

    useEffect(() => {

        axios.get(`/onepiece`, {
            params: {
                input: input
            } 
        }).then((response) => {
            setEpisode(response);
        })

    }, [input])

    console.log(episode);

    return (
        <div className='display'>
            <h1>Title: </h1>
            <h1>{episode?.data?.title}</h1>
            <h1>Chapter: </h1>
            <h1>{episode?.data?.chapter}</h1>
            <h1>Summary: </h1>
            <h1>{episode?.data?.summary}</h1>
            <h1>Characters: </h1>
            <h1>{episode?.data?.characters}</h1>
            <h1>Cover Photo: </h1>
            <img src={episode?.data?.cover_images}/>

        </div>
    )
}

export default Display
