// Initial Data
export const initialState = {
    input: ''
};

const reducer = (state, action) => {
    // 
    console.log(action);

    switch(action.type) {
        case 'ADD-EPISODE':
            return {
                ...state,
                input: action.input
            };
    }
}

export default reducer;
